import React, { useState } from "react";
import { Link } from "react-router-dom";
const Join = () => {
  const [name, setName] = useState("");
  const [room, setroom] = useState("");

  return (
    <div>
      <h1>Join</h1>
      <input type="text" className="name" onChange={setName(e.target.value)} />
      <input type="text" className="room" onChange={setroom(e.target.value)} />
      <button type="submit">Log In</button>
    </div>
  );
};

export default Join;
